        isTablet = getResources().getBoolean(R.bool.isTablet);

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        int listContainerId = isTablet ? R.id.news_list_container : R.id.fragment_container;
        getSupportFragmentManager().beginTransaction()
                .replace(listContainerId, new NewsFragment())
                .commit();

        BottomNavigationView bottomNavigationView = findViewById(R.id.bottom_navigation);

        bottomNavigationView.setOnItemSelectedListener(item -> {
            switch (item.getItemId()) {
                case R.id.navigation_home:
                    loadFragment(new NewsFragment()); // 加载 NewsFragment
                    return true;
                case R.id.navigation_user:
                    // 加载其他 Fragment
                    return true;
            }
            return false;
        });

        // 默认选择 home 并加载 NewsFragment
        bottomNavigationView.setSelectedItemId(R.id.navigation_home);

        if(isTablet) {
            loadTabletFragment();
        } else {
            loadFragment(new NewsFragment());
        }
    }

    private void loadTabletFragment() {
        FragmentManager fragmentManager = getSupportFragmentManager();
        FragmentTransaction transaction = fragmentManager.beginTransaction();

        // 左侧加载新闻列表
        transaction.replace(R.id.news_list_container, new NewsFragment());

        // 右侧加载空详情页或占位Fragment
        transaction.replace(R.id.news_detail_container, new PlaceholderFragment());

        transaction.commit();
    }

    // 加载 Fragment 的方法
    private void loadFragment(Fragment fragment) {
        FragmentManager fragmentManager = getSupportFragmentManager();
        FragmentTransaction transaction = fragmentManager.beginTransaction();
        transaction.replace(R.id.fragment_container, fragment);
        transaction.commit();
    }
}

